mapper.update:
UPDATE USER_TBL
SET USER_ST = 'N'
WHERE USER_ID = #{userId}

UPDATE USER_TBL
SET USER_ST = 'B'
WHERE USER_ID = #{userId}

mapper.select:
SELECT * FROM USER_TBL
WHERE USER_ID = #{userId}
OR USER_EMAIL = #{userEmail}

SELECT * FROM USER_TBL
WHERE USER_EMAIL = #{userEmail}
AND USER_ID = #{userId}

mapper.update:
UPDATE USER_TBL
SET USER_PW = #{userPw}
WHERE USER_ID = #{userId}

UPDATE USER_TBL
SET USER_PW = #{userPw},
ZIP_CODE = #{zipCode},
USER_ADDRESS = #{userAddress},
DETAIL_ADDRESS = #{detailAddress}
WHERE USER_ID = #{userId}
